DELIMITER $$

CREATE FUNCTION IF NOT EXISTS levenshtein(s1 VARCHAR(255), s2 VARCHAR(255)) 
RETURNS INT DETERMINISTIC
BEGIN
  DECLARE s1_len, s2_len, i, j, c, c_temp INT;
  DECLARE s1_char CHAR;
  DECLARE cv0, cv1 VARBINARY(256);

  SET s1_len = CHAR_LENGTH(s1);
  SET s2_len = CHAR_LENGTH(s2);
  SET cv1 = 0x00;

  IF s1 = s2 THEN RETURN 0;
  ELSEIF s1_len = 0 THEN RETURN s2_len;
  ELSEIF s2_len = 0 THEN RETURN s1_len;
  END IF;

  SET cv0 = ;

  SET i = 1;
  WHILE i <= s1_len DO
    SET cv1 = CONCAT(cv1, CHAR(i));
    SET i = i + 1;
  END WHILE;

  SET j = 1;
  WHILE j <= s2_len DO
    SET s1_char = SUBSTRING(s2, j, 1);
    SET c = j;
    SET cv1 = INSERT(cv1, 1, 1, CHAR(j));

    SET i = 1;
    WHILE i <= s1_len DO
      SET c_temp = ORD(SUBSTRING(cv0, i, 1)) + 1;
      IF s1_char = SUBSTRING(s1, i, 1) THEN
        SET c_temp = ORD(SUBSTRING(cv0, i, 1));
      END IF;

      SET c = LEAST(c + 1, ORD(SUBSTRING(cv1, i, 1)) + 1, c_temp);
      SET cv1 = INSERT(cv1, i + 1, 1, CHAR(c));
      SET i = i + 1;
    END WHILE;

    SET cv0 = cv1;
    SET j = j + 1;
  END WHILE;

  RETURN c;
END $$

DELIMITER ;
